import { Component, ComponentFactoryResolver, HostListener,ComponentRef  } from '@angular/core';
import { AddEmployeeComponent } from './add-employee/add-employee.component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'frontend';

  submitted : boolean = false;
  componentRef!: ComponentRef < AddEmployeeComponent > ;
  constructor(private componentFactoryResolver: ComponentFactoryResolver){}

  receiveDataFromChild(data: boolean) {

    this.submitted = data;
    console.log(this.submitted)

  }

  RemoveComponent(){
    window.location.reload();
    // this.componentRef.location.nativeElement()
  }


}

<!-- Button trigger modal -->
<button [routerLink]="['/addEmployee']" type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
  Add Employee
</button>


<app-employee-list></app-employee-list>

<!-- Modal -->
<div  class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <!-- <app-add-employee (childToParent)="receiveDataFromChild($event)" [submitted]="submitted" ></app-add-employee> -->
  <router-outlet></router-outlet>
</div>
