import { Component, ComponentFactoryResolver, HostListener,ComponentRef  } from '@angular/core';
import { AddEmployeeComponent } from './add-employee/add-employee.component';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'frontend';

  submitted : boolean = false;
  formReset : boolean = false
  componentRef!: ComponentRef < AddEmployeeComponent > ;
  constructor(private router: Router){}

  ngOnInit() {
    // this.href = this.router.url;
    console.log(this.router);
}

  receiveDataFromChild(data: boolean) {

    this.submitted = data;
    console.log(this.submitted)

  }

  RemoveComponent(){
    console.log("Running")
    // this.componentRef.location.nativeElement()
  }


}


<div class="row justify-content-between">

  <div class="col-12">
    One of two columns
  </div>
</div>
<!-- Button trigger modal -->
<button (click)="submitted = false; formReset = true" type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
  Add Employee
</button>


<app-employee-list></app-employee-list>

<!-- Modal -->
<div  class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel">
  <app-add-employee (childToParent)="receiveDataFromChild($event)" [submitted]="submitted" [formReset] = "formReset" ></app-add-employee>
  <!-- <router-outlet ></router-outlet> -->
</div>
