import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ApiManagementService {

  AllEmployeeData : any[] = []

  constructor(private http : HttpClient) { }

  ngOnInit():void{
    this.getAllEmployee().subscribe((response:any)=>{
      this.AllEmployeeData = response.detail
    })
  }



  insertEmployee(data:any):Observable<any>{
    return this.http.post('http://localhost:3000/api/employee',data)
  }


  getAllEmployee():Observable<any>{
    return this.http.get('http://localhost:3000/api/employee')
  }
}
